import * as ActionTypes from "../actions/authActions";
const initialState = {
  welcomeData: undefined,
  loading: false,
  error: null
};

const WelcomeMessageReducer = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case ActionTypes.AUTH_START:
      return {
        ...state,
        loading: true,
        error: null
      };

    case ActionTypes.AUTH_SUCCESS:
      return {
        ...state,
        loading: false,
        welcomeData: [action.welcomeData]
      };

    case ActionTypes.AUTH_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error,
        welcomeData: undefined
      };

    default:
      return state;
  }
};

export default WelcomeMessageReducer;
