import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { userActions } from "../../ApiCall/AuthApi";
import "./LoginPage.css";
function LoginPage() {
  const [username, setUser] = useState("");
  const [password, setPass] = useState("");
  const [error, setError] = useState(false);
  const dispatch = useDispatch();

  const authData = useSelector(state => state.AuthReducer);
  console.log(authData);
  const authValidate = event => {
    event.preventDefault();
    if (username && password) {
      dispatch(userActions.auth({ username: username, password: password }));
    } else {
      setError(true);
    }
  };
  const usernameChange = event => {
    setUser(event.target.value);
  };
  const passwordChange = event => {
    setPass(event.target.value);
  };
  return (
    <div className="login-page main">
      <p className="sign" align="center">
        Sign in
      </p>
      <form onSubmit={authValidate} className="form1">
        <input
          className="un"
          type="text"
          align="center"
          onChange={usernameChange}
          value={username}
          placeholder="Username"
          required
        />
        <br></br>
        {error && !username && <span>Please Enter Username</span>}
        <input
          className="pass"
          value={password}
          onChange={passwordChange}
          type="password"
          align="center"
          placeholder="Password"
        />
        <br></br>

        {error && !password && <span>Please Enter Password</span>}
        <button type="submit" className="submit" align="center">
          Sign in
        </button>
      </form>
    </div>
  );
}

export default LoginPage;
